function getProducts(){return firebase.database().ref("/products").once("value").then(function(e){var t=e.val();jQuery.each(t,function(e,t){$("#products").append('<div class="ui fluid card"><div class="image"><img src="'+t.imageUrl+'" alt="'+t.nombre+'" /></div><div class="content"><div class="header">'+t.nombre+"</div><p>"+t.precio+"</p><p>"+t.descripcion+'</p><form action="/charge" method="POST"><script src="https://checkout.stripe.com/checkout.js"class="stripe-button"data-key="pk_test_6pRNASCoBOKtIshFeQd4XMUh"data-image="/img/icon.png"data-name="ISC Online Store"data-description="'+t.nombre+" ($"+t.precio+')"data-amount="'+100*t.precio+'"></script></form></div></div>')})})}getProducts();